package script

import (
	"reflect"
	"testing"

	"github.com/qtumproject/btcd/txscript"
	"github.com/qtumproject/qtool/lib/common"
	"github.com/stretchr/testify/assert"
)

func TestParseParamsOpCall(t *testing.T) {
	assert := assert.New(t)
	// data source: https://qtum.info/tx/d20c5c31536e60decf175caf2cbfba980c3678c0f4b201c9b9fa1440102e6451
	// hex: 540390d003012844095ea7b300000000000000000000000025495b3a87d82e9d7a71b341addfc0d7bb3475c7ffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff1454fefdb5b31164f66ddb68becd7bdd864cacd65bc2
	byteCode := "095ea7b300000000000000000000000025495b3a87d82e9d7a71b341addfc0d7bb3475c7ffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff"
	contractAddr := "54fefdb5b31164f66ddb68becd7bdd864cacd65b"
	disasmScript := "4 90d003 28 095ea7b300000000000000000000000025495b3a87d82e9d7a71b341addfc0d7bb3475c7ffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff 54fefdb5b31164f66ddb68becd7bdd864cacd65b OP_CALL"
	finalOp := txscript.OP_CALL

	contractInvokeInfo, err := ParseContractParams(disasmScript, byte(finalOp))
	assert.Nil(err)

	t.Run("parser returns the correct value in 'to' field ", func(t *testing.T) {
		want := contractAddr
		got := contractInvokeInfo.To
		assert.Equal(want, got)

	})
	t.Run("parser returns the correct value in 'value' field", func(t *testing.T) {
		want := byteCode
		got := contractInvokeInfo.CallData
		assert.Equal(want, got)
	})

}

func TestParseParamsSenderOpCall(t *testing.T) {
	// 1 <sender's pubkeyhash address> < {signature, pubkey}> OP_SENDER <evm version>  <gas limit> <gas price> [byte code] <contract address> OP_CALL
	// https://qtum.info/tx/0425fa39feed4cd6c93998159901095c147f8b0043823067dc1d25dabf950ac9
	assert := assert.New(t)

	disasmScript := "01 93594441cb5de8b497ad8467d55412c2a0ef3659 6a4730440220396b30b7a2f2af482e585473b7575dd2f989f3f3d7cdee55fa34e93f23d5254d022055326cdcab38c58dc3e65c458bfb656cca8340f59534c00ad98b4d4d3303f459012103379c39b6fb2c705db608f98a8fc064f94c66faf894996ca88595487f9ef04a6e OP_SENDER 04 90d003 28 3d666e8b 0000000000000000000000000000000000000086 OP_CALL"
	contractAddr := "0000000000000000000000000000000000000086"
	from := "93594441cb5de8b497ad8467d55412c2a0ef3659"
	byteCode := "3d666e8b"
	finalOp := txscript.OP_CALL

	got, err := ParseContractParams(disasmScript, byte(finalOp))
	assert.NoError(err)
	want := &ContractInvokeInfo{
		From:     from,
		GasLimit: "3d090",
		GasPrice: "28",
		CallData: byteCode,
		To:       contractAddr,
	}
	if !reflect.DeepEqual(got, want) {
		t.Errorf(
			"parse transaction call sam error\ninput: %s\nwant: %s\ngot: %s",
			disasmScript,
			string(common.MustMarshalIndent(want, "", "  ")),
			string(common.MustMarshalIndent(got, "", "  ")),
		)
	}

}

